mapper.update:
update transportation
set
	trans_category = #{category}
,	start_location = #{startLocation}
,	start_time = #{startTime}
,	end_location = #{endLocation}
,	end_time = #{endTime}
,	trans_memo = #{memo}
where
	trans_key = #{transKey}

update planner_wallet
set
	wallet_title = #{title}
where
	wallet_frg_key = #{frgKey}
