mapper.select:
select distinct P.plan_no as "no" , P.user_id as userId , P.plan_title as title , P.plan_start as startDate , p.plan_end as endDate , P.plan_member as "member" , P.plan_status as status , P.plan_open as "open" , P.plan_hashtag as hashTag , P.create_date as createDate , U.user_profile as "profile.profile" from planner P , planner_location C , locations L , (select plan_no, count(*) as cnt from planner_daily group by plan_no) N , planner_user U where P.plan_no = C.plan_no and C.location_id = L.location_id and P.plan_status = 'Y' and P.plan_open = 'Y' and P.plan_no = N.plan_no and P.user_id = U.user_id
and P.plan_title like '%' || #{searchTitle} || '%'
and P.user_id like '%' || #{searchId} || '%'
and L.location_name in
#{item}
and P.plan_member = #{searchMemebers}
and N.cnt = #{searchTerm}
and P.plan_hashtag like '%'|| #{tag} || '%'
order by P.plan_no desc
